//Reverse an array without taking extra space
#include <stdio.h>
int main(){
int i,n;
int array[20];
printf("enter the number of elements of array : ");
scanf("%d",&n);
printf("enter the elements ");
for(int i=0;i<=n;i++){
    scanf("%d ",&array[i]);
}

for(int i=5;i>=0;i--){
    printf("%d",array[i]);
}
printf("%d ",array[i]);









}